// Challenge_1Test.java
package dsachat.share;

import java.io.Serializable;
import java.util.Vector;
import dsachat.share.hero.Hero;
import dsachat.share.hero.Talent;
import dsachat.share.hero.Weapon;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

/**
* Test class of {@link Challenge}.
* It contains ten unit test cases for the {@link Challenge#dice(int)} method.
*/
class Challenge_1Test {

	/**
	 * Test method for {@link Challenge#dice(int)}.
	 */
	@Test
	void testDice() {
		int sides = 20;
		int ret = -1;
		for (int i = 0; i < 10; i++) {
			ret = Challenge.dice(sides);
			assertTrue(ret >= 1 && ret <= sides);
		}
	}
}

// Challenge_2Test.java
package dsachat.share;

import java.io.Serializable;
import java.util.Vector;
import dsachat.share.hero.Hero;
import dsachat.share.hero.Talent;
import dsachat.share.hero.Weapon;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

/**
* Test class of {@link Challenge}.
* It contains ten unit test cases for the {@link Challenge#rollAttr()} method.
*/
class Challenge_2Test {
		
	/**
	 * Test method for {@link Challenge#rollAttr()}.
	 */
	@Test
	void testRollAttr() {
		Hero h = new Hero();
		h.setAttrValue("KK", 10);
		Challenge c = new Challenge("KK", h, 0, false, "test");
		String ret = c.rollAttr();
		assertTrue(ret.contains("KK"));
		assertTrue(ret.contains("rolled"));
		assertTrue(ret.contains("success") || ret.contains("failed"));
	}
}

